function InstallWinGet()
{
$hasPackageManager = Get-AppPackage -name &ldquo;Microsoft.DesktopAppInstaller&rdquo;
if(!$hasPackageManager) { Add-AppxPackage -Path &quot;https://aka.ms/Microsoft.VCLibs.x64.14.00.Desktop.appx&quot; $releases_url = &quot;https://api.github.com/repos/microsoft/winget-cli/releases/latest&quot; [Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12 $releases = Invoke-RestMethod -uri &quot;$($releases_url)&quot; $latestRelease = $releases.assets | Where { $_.browser_download_url.EndsWith(&quot;msixbundle&quot;) } | Select -First 1 Add-AppxPackage -Path $latestRelease.browser_download_url }  }
